import React from "react";
import {Card as AntdCard, Space, Tag, Image} from 'antd'
import { StarFilled } from "@ant-design/icons";
import cls from './Card.module.scss'
import { Link, useNavigate } from "react-router-dom";
import { getRouteReviewDetails } from "@/shared/const/router";
import { Review, getSetReviewEditState } from "@/entities/Review";

const { Meta } = AntdCard;

export const Card = (props: Review) => {
    const {
        ReviewText,
        ReviewName,
        TitleOfWork,
        AuthRating,
        TypeOfWork,
        Tags, 
        id
    } = props
    
    const navigate = useNavigate()
    const setReviewEditState = getSetReviewEditState()

    const onTag: React.MouseEventHandler<HTMLDivElement> = (e) => {
        e.stopPropagation()
        console.log('tag', e.currentTarget.outerText )
    }

    const regex = /<img\s[^>]*?src="([^"]*?)"/;
    const showcase = ReviewText?.match(regex) ?? '';

    const onDetails = () => {
        if (id) { 
            const tags = Tags.map(value => value.tag)
            navigate(getRouteReviewDetails(id))
            setReviewEditState({ReviewText,
                ReviewName,
                TitleOfWork,
                AuthRating,
                TypeOfWork,
                Tags: tags, 
                id 
            })
        }
    }

    if (!props) {
        return <div> No data</div>
    }

    return (
        <AntdCard 
            className={cls.AntdCard}
            onClick={onDetails}
            size={'default'}
            extra={<Link to={getRouteReviewDetails('1')}>More</Link>}
            hoverable
            title={ReviewName}
            style={{ width: 240 }}
            cover={
                <Image
                // width={200}
                height={240}
                preview={false}
                src={showcase[1]}
                fallback="data:image/png;base64,"
              />
                // <img 
                //     alt="example" 
                //     src={showcase[1] || 'https://funart.pro/uploads/posts/2020-06/1592795013_p-multfilm-rango-42.jpg'} 
                //     className={cls.image}    
                // />
                }
        >
            <Space  direction={'vertical'} style={{width: '100%'}}  >
                <Meta 
                    title={TitleOfWork}  
                />
                <div className={cls.rating}>
                    <span>
                        {TypeOfWork} 
                    </span>
                    <Space>
                        {`${AuthRating}/5`} 
                        <StarFilled className={cls.ratingStar} />
                    </Space>
                </div>
                <div className={cls.tags}>
                    {Tags?.map(({tag}) => (
                    <Tag 
                        key={tag}
                        onClick={onTag}
                        className={cls.tag}
                    >
                        {tag}
                    </Tag>
                    ))}
                </div>  
            </Space>
        </AntdCard>
    )
}
